SELECT *
FROM chat_history
WHERE appId = #{req.appId}

    AND userId = #{req.userId}


    AND createTime < #{req.lastCreateTime}


    AND messageType = #{req.messageType}

ORDER BY createTime DESC
LIMIT #{req.pageSize}

SELECT COUNT(*)
FROM chat_history


        id = #{req.id}


        AND appId = #{req.appId}


        AND message LIKE CONCAT('%', #{req.message}, '%')


        AND messageType = #{req.messageType}


        AND userId = #{req.userId}

